mapper.insert:

insert into t_sys_user (id, nickname, username, 
  `password`, `admin`, create_date, 
  modified_time, `status`, is_delete
  )
values (#{id,jdbcType=BIGINT}, #{nickname,jdbcType=VARCHAR}, #{username,jdbcType=VARCHAR}, 
  #{password,jdbcType=VARCHAR}, #{admin,jdbcType=INTEGER}, #{createDate,jdbcType=TIMESTAMP}, 
  #{modifiedTime,jdbcType=TIMESTAMP}, #{status,jdbcType=INTEGER}, #{isDelete,jdbcType=TINYINT}
  )

mapper.select:
select id, nickname, username, `password`, `admin`, create_date, modified_time, `status`,
is_delete
from t_sys_user where username=#{userName,jdbcType=VARCHAR}
